import React from 'react';
import styled from 'styled-components';

interface FieldsetProps {
  legendText: string,
  index: number,
}

interface LegendProps {
  index: number,
}

const LegendStyle = styled.legend`
  display: flex;
`;

const CircleIcon = styled.span`
  width: 45px;
  height: 45px;
  border-radius: 50%;
  color: #007079;
  background-color: #CCE2E4;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 20px;
`;

const Legend: React.FC<LegendProps> = ({index, children}) => {
  return (
    <LegendStyle>
      <CircleIcon>
        {index}
      </CircleIcon>
      {children}
    </LegendStyle>
  );
};

export const Fieldset: React.FC<FieldsetProps> = ({legendText, children, index}) => {
  return (
    <fieldset>
      <Legend index={index}>{legendText}</Legend>
      {children}
    </fieldset>
  );
};
