import React from 'react';
import {FormSection} from './FormSection';
import {Label, TextInput} from './Inputs';
import styled from 'styled-components';

const ShipInputs = styled.span`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

export const CalculateFormContainer = () => {
  return (
    <form>
      <FormSection legendText='Ship' index={1}>
        <ShipInputs>
          <p>
            <Label htmlFor='ship-name'>
              Name
            </Label>
            <TextInput id='ship-name' placeholder='Name'/>
          </p>
          <p>
            <Label htmlFor='ship-country'>
              Country
            </Label>
            <TextInput id='ship-country' placeholder='Country'/>
          </p>
        </ShipInputs>
      </FormSection>
    </form>
  );
};
